var scopes = 'https://www.googleapis.com/auth/userinfo.profile%20https://www.google.com/m8/feeds/%20https://www.googleapis.com/auth/contacts.readonly';
var redirectUri = (!!config.LOCAL ? config.BASE_SERVER_URL : (!config.ENDPOINT_CONFIG['clientType'] || config.ENDPOINT_CONFIG['clientType'] == 'single' ? config.SERVER_URL : config.BASE_SERVER_URL))+'/callback';
var url = 'https://accounts.google.com/o/oauth2/auth?access_type=offline&approval_prompt=force&client_id='+config.ENDPOINT_CONFIG['clientId']+'&redirect_uri='+redirectUri+'&response_type=code&scope='+scopes+'&state='+config.APP;
var win = window.open(url, 'Authorization page', 'toolbar=no,scrollbars=no,location=no,statusbar=no,menubar=no,resizable=0,width=500,height=600,left='+((screen.width/2)-250)+',top='+((screen.height/2)-250)+',');

var self = this;

var intervalFn = function() {
	try {
		if (!win || !win.document) {
			window.clearInterval(pollTimer);
		}else{
			if (win.document.URL.indexOf('/callback') != -1) {
				win.innerWidth = 100;
				win.innerHeight = 100;
				win.screenX = screen.width;
				win.screenY = screen.height;
				window.clearInterval(pollTimer);
				url = win.document.URL;
				var pos = url.indexOf('code=');
				if(pos > -1) {
					var token = url.substring(pos + 5);
					config.code = token;
					config.redirectUri = redirectUri;
					if (utils.connectToService) {
						utils.connectToService();
					}
				}
				try{win.close();}catch(e){}
			}
		}
	} catch(e) {}
};

var pollTimer = window.setInterval(function() {intervalFn.apply(self)}, 500);
